 # data analysis and wrangling import numpy as np # linear algebra import pandas as pd # data processing, CSV file I/O (e.g. pd.read_csv) # import random as rnd # visualization import seaborn as sns import matplotlib.pyplot as plt # %matplotlib inline # machine learning from sklearn.linear_model import LinearRegression from sklearn.metrics import r2_score from sklearn.metrics import mean_squared_error import statsmodels.api as sm import itertools # itertools functions create iterators for efficient looping # itertools.combinations(p,r) creates r-length tuples, in sorted order, no repeated elements # ex. combinations(&#39;ABCD&#39;, 2) = [AB AC AD BC BD CD] #from IPython.display import Image # to visualize images #from tabulate import tabulate # to create tables import os for dirname, _, filenames in os.walk(&#39;/kaggle/input&#39;): for filename in filenames: print(os.path.join(dirname, filename)) /kaggle/input/prostate-data/tab4.png /kaggle/input/prostate-data/tab4-no-cap.png /kaggle/input/prostate-data/tab3.png /kaggle/input/prostate-data/prostate.data /kaggle/input/prostate-data/tab.png /kaggle/input/prostate-data/tab2.png 1. Open your kernel SL_EX2_ProstateCancer_Surname in Kaggle 2. Generate a copy called SL_EX3_SubsetSelection_Surname by the Fork button Data acquisition ## Load the Prostate Cancer dataset data = pd.read_csv(&#39;../input/prostate-data/prostate.data&#39;,sep=&#39;	&#39;) # Save &#34;train&#34; and lpsa&#34; columns into Pandas Series variables train = data[&#39;train&#39;] lpsa = data[&#39;lpsa&#39;] # Drop &#34;train&#34; and lpsa&#34; variable from data data = data.drop(columns=[&#39;Unnamed: 0&#39;,&#39;lpsa&#39;,&#39;train&#39;],axis=1) Data pre-processing ### X VARIABLE: # Split the data in train and test sets dataTrain = data.loc[train == &#39;T&#39;] # Obviously, len(idx)==len(dataTrain) is True! dataTest = data.loc[train == &#39;F&#39;] # Rename these two variables as &#34;predictorsTrain&#34; and &#34;predictorsTest&#34; predictorsTrain = dataTrain predictorsTest = dataTest ## Y VARIABLE: # Split the &#34;lpsa&#34; in train and test sets lpsaTrain = lpsa.loc[train == &#39;T&#39;] lpsaTest = lpsa.loc[train == &#39;F&#39;] Standardization \[\dfrac{predictors - predictorsMeans}{predictorsStd}\]
# Standardize &#34;predictorsTrain&#34; predictorsTrainMeans = predictorsTrain.mean() predictorsTrainStds = predictorsTrain.std() predictorsTrain_std = (predictorsTrain - predictorsTrainMeans)/predictorsTrainStds # standardized variables of predictorTrain # Standardize &#34;predictorsTest&#34; (using the mean and std of predictorsTrain, it&#39;s better!) predictorsTest_std = (predictorsTest - predictorsTrainMeans)/predictorsTrainStds # standardized variables of predictorTest Split into Training and Test sets ## TRAINING SET X_train = predictorsTrain_std Y_train = lpsaTrain ## TEST SET X_test = predictorsTest_std Y_test = lpsaTest Useful functions for the analysis of the Prostate_Cancer_dataset #For Linear Regression # Useful functions in order to compute RSS, R_squared and Zscore. def LinReg(X_train,Y_train,X_test,Y_test): # Create the linear model, fitting also the intecept (non-zero) model = LinearRegression(fit_intercept = True) # Train the model on training set model.fit(X_train,Y_train) # Stats on Training set Y_train_pred = model.predict(X_train) RSS_train = mean_squared_error(Y_train,Y_train_pred) * len(Y_train) R2_train = model.score(X_train,Y_train) # Stats on Test set Y_test_pred = model.predict(X_test) RSS_test = mean_squared_error(Y_test,Y_test_pred) * len(Y_test) R2_test = model.score(X_test,Y_test) return RSS_train, RSS_test, R2_train def Zscore(X_train,Y_train): # fitting the model model = sm.OLS(Y_train, sm.add_constant(X_train)).fit() Zscores = model.tvalues[1:] # we don&#39;t want const min_Zscore = min(abs(Zscores)) idx_min_Zscore = abs(Zscores).idxmin() # it&#39;s the nearest to zero, so the variable less significant! return Zscores, min_Zscore, idx_min_Zscore To print some info ## Print information about an iteration forward subset selection with subset size ncomb # ncomb = number of the iteration # features = remaining_features, selected_features (used to do the iteration and take track of the selection) # params = all_RSS, all_R_squared, all_c